Value.Value:
v1(data, context) {
  return data.value ? data.value : new Date();
}

v1(data, context) {
  var result = data.default + ' ' + new Date(data.PublicationMoment || null).toLocaleString();
  if(data.UrlKey) result += ' .../' + data.UrlKey;
  return result;
}

v1(data, context) {
  if(context.cache.secondRun) return data.value;
  context.cache.secondRun = true;

  return !!(data.MetaTitle || data.MetaDescription || data.SharingDescription);
}

v1(data, context) {
  const addOn = [];
  addOn.push('🏷️ ' + data.Categories.length + '/' + data.Tags.length);
  if(data.ShowOnStartPage) addOn.push('🏠');
  return data.default + addOn.join(' ');
}
